import {Component, OnInit} from '@angular/core';
import {FormBuilder, FormGroup, Validators} from "@angular/forms";
import {MyValidatorsService} from "../../services/my-validators.service";
import {CategoryService} from "../admin-services/category.service";

@Component({
  selector: 'app-new-category',
  templateUrl: './new-category.component.html',
  styleUrls: ['./new-category.component.scss']
})
export class NewCategoryComponent implements OnInit{

  categoryFormGroup !: FormGroup;

  constructor(private fb: FormBuilder,
              public myValService: MyValidatorsService,
              private catService: CategoryService) { }

  ngOnInit() {

    this.categoryFormGroup = this.fb.group({
      name: ['', Validators.required],
      description: ['', Validators.required]
    })

  }

  handleSubmit() {
    let name = this.categoryFormGroup.value.name;
    let description = this.categoryFormGroup.value.description;

    this.catService.addCategory(name, description).subscribe({
      next : value => {
        alert("Category successfully registered !")
      },
      error: err => {
        console.log(err)
      }
    })
  }
}
<div class="form_container">

  <form [formGroup]="categoryFormGroup" (ngSubmit)="handleSubmit()">
    <h3>ADD CATEGORY</h3>
    <div class="form_lines">
      <label for="name">Name </label>
      <div class="input_div">
        <input placeholder="Ex: choes" type="text" id="name" formControlName="name">
        <small *ngIf="categoryFormGroup.controls['name'].errors && categoryFormGroup.controls['name'].touched" class="text-danger">
          {{ myValService.getValidatorErrorMessage('Name', categoryFormGroup.controls['name'].errors) }}
        </small>
      </div>
    </div>
    <div class="form_lines">
      <label for="description">Description </label>
      <div class="input_div">
        <input placeholder="Enter the description of the product" type="text" id="description" formControlName="description">
        <small *ngIf="categoryFormGroup.controls['description'].errors && categoryFormGroup.controls['description'].touched" class="text-danger">
          {{ myValService.getValidatorErrorMessage('Description', categoryFormGroup.controls['description'].errors) }}
        </small>
      </div>
    </div>
    <button type="submit">Register</button>
  </form>
</div>
