import { Injectable } from '@angular/core';
import {environment} from "../../../environments/environment";
import {Product} from "../../shared/model/Product";
import {HttpClient} from "@angular/common/http";
import {Observable} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class ProductService {

  apiUrl = "";

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.apiUrl = environment.apiUrl;
  }

  addImage(formData: FormData): Observable<any>{
    return  this.http.post(this.apiUrl + "/uploadFile", formData);
  }

  handleAddProduct(categoryId: number, name: string, price: number, description: string, imageUrl: string, stockQte: number, productStatus: string){
    let product = {
      name,
      price,
      description,
      imageUrl,
      stockQte,
      productStatus
    }
    return this.http.post(this.apiUrl + "/product/add?categoryId="+categoryId, product);
  }

}
