import {Injectable, OnInit} from '@angular/core';
import {HttpClient} from "@angular/common/http";
import {Observable} from "rxjs";
import {environment} from "../../../environments/environment";

@Injectable({
  providedIn: 'root'
})
export class ProductService implements OnInit{

  apiUrl = "";

  constructor(private http: HttpClient) { }

  ngOnInit() {
    this.apiUrl = environment.apiUrl;
    console.log("Production => ", environment.production)
    console.log("API URL : ", this.apiUrl)
  }

  addImage(formData: FormData): Observable<any>{
    return  this.http.post(this.apiUrl + "/uploadFile", formData);
  }

  handleAddProduct(categoryId: number, name: string, price: number, description: string, imageUrl: string, stockQte: number, productStatus: string){
    let product = {
      name,
      price,
      description,
      imageUrl,
      stockQte,
      productStatus
    }
    return this.http.post(this.apiUrl + "/product/add?categoryId="+categoryId, product);
  }

}
