import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import {ProductService} from "../admin-services/product.service";
import {ActivatedRoute} from "@angular/router";
import {MyValidatorsService} from "../../services/my-validators.service";
import {Category} from "../../shared/model/category";
import {CategoryService} from "../admin-services/category.service";

@Component({
  selector: 'app-edit-food',
  templateUrl: './edit-food.component.html',
  styleUrls: ['./edit-food.component.scss']
})
export class EditFoodComponent implements OnInit{

  public productFormGroup!: FormGroup;
  private productId!: number;
  selectedFile! : File;
  categoryList: Category[] = [];
  productStatus: any[] = [];

  constructor(private fb: FormBuilder,
              private route: ActivatedRoute,
              public myValService: MyValidatorsService,
              private productService: ProductService,
              private catService: CategoryService) { }

  ngOnInit(): void {
    this.productStatus = [
      {name: "CREATED", value: "CREATED"},
      {name: "AVAILABLE", value: "AVAILABLE"},
      {name: "NOT AVAILABLE", value: "NOT_AVAILABLE"}
    ]
    this.route.paramMap.subscribe(params => {
        this.productId = Number(params.get("id"));
    })
    this.productFormGroup = this.fb.group({
      name: ['', Validators.required],
      category: ['', Validators.required],
      price: ['', Validators.required, Validators.min(0)],
      description: ['', Validators.required],
      image: ['', Validators.required],
      quantity: ['', Validators.required],
      productStatus: ['', Validators.required],
    });

    this.catService.getCategories().subscribe({
      next: cats => {
        this.categoryList = cats;
      }
    })

    this.productService.getProductById(this.productId).subscribe({
      next: p => {
        console.log("Product: ", p);
        this.productFormGroup.patchValue({name: p.name});
        this.productFormGroup.patchValue({category: p.category.categoryId});
        this.productFormGroup.patchValue({price: p.price});
        this.productFormGroup.patchValue({description: p.description});
        this.productFormGroup.patchValue({image: p.imageUrl});
        this.productFormGroup.patchValue({quantity: p.stockQte});
        this.productFormGroup.patchValue({productStatus: p.productStatus});
      }
    })
  }

  onFileSelected(event: any){
    this.selectedFile = event.target.files[0];
  }

  handleSubmit() {
    let name = this.productFormGroup.value.name;
    let categoryId = this.productFormGroup.value.category;
    let price = this.productFormGroup.value.price;
    let description = this.productFormGroup.value.description;
    let quantity = this.productFormGroup.value.quantity;
    let productStatus = this.productFormGroup.get('productStatus')?.value;
    let imageUrl = null;

    console.log("Product status: ", productStatus)

    // Ajout de l'image
    const formData = new FormData();
    formData.append('file', this.selectedFile);
    this.productService.addImage(formData).subscribe({
      next: (value: any) => {
        imageUrl = value.fileDownloadUri;
        this.productService.updateProduct(this.productId, categoryId, name, price, description, imageUrl, quantity, productStatus)
          .subscribe({
            next: prod => {
              alert("Product successfully update!");
            },
            error: err => {
              alert("Error: Product not add correctly");
              console.log(err)
            }
          })
      }
    })


  }
}
<div class="form_container">
  <form [formGroup]="productFormGroup" (ngSubmit)="handleSubmit()">
    <h3>UPDATE PRODUCT</h3>
    <div class="form_lines">
      <label for="name">Name </label>
      <div class="input_div">
        <input placeholder="Ex: choes" type="text" id="name" formControlName="name">
        <small *ngIf="productFormGroup.controls['name'].errors && productFormGroup.controls['name'].touched" class="text-danger">
          {{ myValService.getValidatorErrorMessage('Name', productFormGroup.controls['name'].errors) }}
        </small>
      </div>
    </div>
    <div class="form_lines">
      <label for="category">Category </label>
      <select formControlName="category" id="category">
        <option *ngFor="let cat of categoryList" [value]="cat.categoryId">{{ cat.name }}</option>
      </select>
    </div>
    <div class="form_lines">
      <label for="price">Price </label>
      <div class="input_div">
        <input placeholder="Ex: 20" type="number" id="price" formControlName="price">
        <small *ngIf="productFormGroup.controls['price'].errors && productFormGroup.controls['price'].touched" class="text-danger">
          {{ myValService.getValidatorErrorMessage('Price', productFormGroup.controls['price'].errors) }}
        </small>
      </div>
    </div>
    <div class="form_lines">
      <label for="description">Description </label>
      <div class="input_div">
        <input placeholder="Enter the description of the product" [value]="productFormGroup.get('description')?.value" type="text" id="description" formControlName="description">
        <small *ngIf="productFormGroup.controls['description'].errors && productFormGroup.controls['description'].touched" class="text-danger">
          {{ myValService.getValidatorErrorMessage('Description', productFormGroup.controls['description'].errors) }}
        </small>
      </div>
    </div>
    <div class="form_lines">
      <label for="image">Image </label>
      <div class="input_div">
        <input type="file" id="image" [src]="productFormGroup.get('imageUrl')?.value" (change)="onFileSelected($event)" formControlName="image">
        <small *ngIf="productFormGroup.controls['image'].errors && productFormGroup.controls['image'].touched" class="text-danger">
          {{ myValService.getValidatorErrorMessage('Image', productFormGroup.controls['image'].errors) }}
        </small>
      </div>
    </div>
    <div class="form_lines">
      <label for="quantity">Quantity</label>
      <div class="input_div">
        <input type="number" id="quantity" formControlName="quantity">
        <small *ngIf="productFormGroup.controls['quantity'].errors && productFormGroup.controls['quantity'].touched" class="text-danger">
          {{ myValService.getValidatorErrorMessage('Quantity', productFormGroup.controls['quantity'].errors) }}
        </small>
      </div>
    </div>
    <div class="form_lines">
      <label for="productStatus">Product Status </label>
      <select formControlName="productStatus" id="productStatus">
        <option *ngFor="let status of productStatus" [value]="productFormGroup.get('productStatus')?.value">{{ status.name }}</option>
      </select>
    </div>
    <button type="submit">Register</button>
  </form>
</div>
